import React, {useState} from "react";
import HomeScreen from "../components/HomeScreen";
import ContactScreen from "../components/ContactScreen";
import OfferScreen from "../components/OfferScreen";
import BaseLayout from "../components/BaseLayout";
import {Routes} from "./Routes";
import GalleryScreen from "../components/GalleryScreen";
import {UserNavigationContext} from "../context/UserNavigationContext";

export default function Navigation() {
    const [currentPage, setCurrentPage] = useState(Routes.HOME)
    const currentPageName = {background: 'red'}

    const Page = () => {
        switch (currentPage) {
            case Routes.HOME:
                return <HomeScreen routeName={currentPage}/>
            case Routes.OFFER:
                return <OfferScreen/>
            case Routes.CONTACT:
                return <ContactScreen/>
            case Routes.GALLERY:
                return <GalleryScreen/>
            default:
                return '404'
        }
    }

    return (
        <UserNavigationContext.Provider value={currentPageName}>
            <BaseLayout navigationState={{currentPage, setCurrentPage}}>
                <Page/>
            </BaseLayout>
        </UserNavigationContext.Provider>

    )
}
