import React, {useState} from "react";
import HomeScreen from "../components/HomeScreen";
import ContactScreen from "../components/ContactScreen";
import OfferScreen from "../components/OfferScreen";
import BaseLayout from "../components/BaseLayout";
import {Routes} from "./Routes";

export default function Navigation(){
    const [currentPage, setCurrentPage] = useState(Routes.HOME)

    const Page = () => {
        switch (currentPage){
            case Routes.HOME:
                return <HomeScreen setCurrentPage={setCurrentPage}/>
            case Routes.OFFER:
                return <OfferScreen setCurrentPage={setCurrentPage}/>
            case Routes.CONTACT:
                return <ContactScreen setCurrentPage={setCurrentPage}/>
            default:
                return '404'
        }
    }

    return (
        <BaseLayout setCurrentPage={setCurrentPage}>
            <Page/>
        </BaseLayout>
    )
}
